originalpath="$vault_path"
user="$(eval echo ~$USER)"
expanded=${originalpath/\~/"$user"} 

results=("${(@f)$(mdfind -onlyin "$expanded" $1)}")

json_obj='{"items":[{}'

for path in "${results[@]}"
do
title=${path##*/}
title="${title%.*}"

json_obj="$json_obj",{\"type\":\"file\",\"title\":\""${title}"\",\"subtitle\":\""${path}"\",\"arg\":\""${path}"\"}

done

json_obj="$json_obj"]}

echo "$json_obj"

#!/usr/bin/python3

import os,re, sys, json

searchstring = sys.argv[1].lower()
json_obj = {"items":[]}

rootdir = "/Users/migueltavares/Dropbox/Notas"

for root, dirs, files in os.walk(rootdir):
	for file in files:
		f = open(os.path.join(root, file))
		lines = f.readlines()
		f.close()

		for line in lines:

			if searchstring in line.lower():
				json_obj["items"].append({
				"type":"file",
				"title": line,
				"subtitle":file,
				"arg":""
				})



out = json.dumps(json_obj)

sys.stdout.write(out)
